<div x-data="{ show: false }" x-init="@if(session('status')) show = true; setTimeout(() => { show = false }, 3000); @endif" x-show="show" class="fixed bottom-4 right-4 bg-green-500 text-white py-2 px-4 rounded shadow-lg transition-opacity duration-300 ease-in-out" x-transition:enter="transition ease-out duration-300" x-transition:enter-start="opacity-0" x-transition:enter-end="opacity-100" x-transition:leave="transition ease-in duration-300" x-transition:leave-start="opacity-100" x-transition:leave-end="opacity-0">
    {{ session('status') }}
</div>